SELECT rownum as no, '' AS ERROR_MSG, UPLOAD_DE, UPLOAD_FILE_NM, EXCEL_ROW_NUM, SELE_YY, ERND_BSNS_CD, SELE_YY_BSNS_CD, ERND_BSNS_NAME_1, ERND_BSNS_NAME_2, ERND_BSNS_NAME_3, ERND_BSNS_NAME_4, DBRAIN_CD, MOEF_DBRAIN_CD, ERND_DBRAIN_CD, BSNS_YY, JRSD_CD, JRSD_NAME, ACTG_CD, ACTG_NAME, FILD_NAME, FILD_CD, SECTOR_NAME, SECTOR_CD, PROGRAM_ID, PROGRAM_NAME, PROGRAM_CODE, UNIT_ID, UNIT_NAME, UNIT_CD, PTC_ID, PTC_NAME, PTC_CD, BSNS_CD, BUD_AM, STAC_AM, BRDN_BSNS_NAME, BRDN_BSNS_NAME2, IRIS_BSNS_CD, YY, PROGRAM_BSNS_CD, UNIT_BSNS_CD, PTC_BSNS_CD, PTC_BSNS_NAME, Z1, /* 오류여부 테스트용 */ CASE WHEN MOD(ROWNUM, 2) = 1 THEN 'Y' ELSE 'N' END AS ERROR_YN FROM IRIS_BSNS_CD_MAP_TEMP WHERE 1=1
