mapper.select:
SELECT ROWNUM AS NO, A.* FROM ( SELECT UPLOAD_DE, UPLOAD_FILE_NM, EXCEL_ROW_NUM, , CASE WHEN (SELECT COUNT(*) FROM IRIS_ITEPD_MAP IM WHERE IM.ERND_IOE_CD = IMT.ERND_IOE_CD AND IM.IRIS_ITEPD_GRP_CD = IMT.IRIS_ITEPD_GRP_CD AND IM.IRIS_ITEPD_CD = IMT.IRIS_ITEPD_CD ) > 0 THEN '기등록' WHEN (SELECT COUNT(*) FROM IRIS_ITEPD_MAP_TEMP IIMT WHERE IIMT.ERND_IOE_CD = IMT.ERND_IOE_CD AND IIMT.IRIS_ITEPD_CD = IMT.IRIS_ITEPD_CD AND IIMT.IRIS_ITEPD_GRP_CD = IMT.IRIS_ITEPD_GRP_CD AND IMT.IRIS_ITEPD_GRP_CD = IIMT.IRIS_ITEPD_GRP_CD AND IIMT.UPLOAD_DE = IMT.UPLOAD_DE AND IIMT.UPLOAD_FILE_NM = IMT.UPLOAD_FILE_NM) > 1 THEN '중복' /* 비목코드 미존재 확인 추가 */ ELSE '' END AS ERROR_MSG, CASE WHEN (SELECT COUNT(*) FROM IRIS_ITEPD_MAP IM WHERE IM.ERND_IOE_CD = IMT.ERND_IOE_CD AND IM.IRIS_ITEPD_GRP_CD = IMT.IRIS_ITEPD_GRP_CD AND IM.IRIS_ITEPD_CD = IMT.IRIS_ITEPD_CD ) > 0 THEN 'Y' WHEN (SELECT COUNT(*) FROM IRIS_ITEPD_MAP_TEMP IIMT WHERE IIMT.ERND_IOE_CD = IMT.ERND_IOE_CD AND IIMT.IRIS_ITEPD_CD = IMT.IRIS_ITEPD_CD AND IIMT.IRIS_ITEPD_GRP_CD = IMT.IRIS_ITEPD_GRP_CD AND IMT.IRIS_ITEPD_GRP_CD = IIMT.IRIS_ITEPD_GRP_CD AND IIMT.UPLOAD_DE = IMT.UPLOAD_DE AND IIMT.UPLOAD_FILE_NM = IMT.UPLOAD_FILE_NM) > 1 THEN 'Y' /* 비목코드 미존재 확인 추가 */ ELSE 'N' END AS ERROR_YN FROM IRIS_ITEPD_MAP_TEMP IMT WHERE 1=1
AND UPLOAD_DE = #{uploadDe}
AND UPLOAD_FILE_NM = #{uploadFileNm}
ORDER BY EXCEL_ROW_NUM ) A

SELECT ROWNUM AS NO,
FROM IRIS_ITEPD_MAP WHERE 1=1
AND ERND_IOE_CD = #{erndIoeCd}
AND IRIS_ITEPD_CD = #{irisItepdCd}
AND ERND_IOE_NM LIKE '%'||UPPER(#{erndIoeNm})||'%'
AND IRIS_ITEPD_CD_NM LIKE '%'||UPPER(#{irisItepdCdNm})||'%'

mapper.insert:
INSERT INTO IRIS_ITEPD_MAP
( NO,

) VALUES
(
  (SELECT NVL(MAX(NO),0)+1 FROM IRIS_ITEPD_MAP),

)

mapper.delete:
DELETE FROM IRIS_ITEPD_MAP_TEMP
WHERE UPLOAD_DE = #{uploadDe}
AND UPLOAD_FILE_NM = #{uploadFileNm}
