SELECT ROWNUM AS NO, ANCM_ID AS IRIS_ANCM_NO, ANCM_SEQ AS ERND_ANCM_NO, BIZ_YR AS ERND_BIZ_YR, ERND_BIZ_CD AS ERND_BIZ_CD FROM IRIS_ANCM_MAP WHERE 1=1
AND ANCM_SEQ = #{erndAncmNo}
AND ANCM_ID = #{irisAncmNo}
AND BIZ_YR = #{erndBizYr}
OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT  ROWNUM AS NO,
        ANCM_ID AS IRIS_ANCM_NO,
        ANCM_SEQ AS ERND_ANCM_NO,
        BIZ_YR AS ERND_BIZ_YR,
        ERND_BIZ_CD AS ERND_BIZ_CD
FROM IRIS_ANCM_MAP
WHERE 1=1

    AND ANCM_SEQ = #{erndAncmNo}


